mapper.update:
update prizes 
   set stock=stock-1,
       modifiedTime=now()
 where id=#{prizeID}
   and stock>0;

mapper.delete:
delete from 
activitiesPrizes
where activityID=#{activityID} and prizeID=#{prizeID};

mapper.select:
select b.id,b.orderNumber,b.name,b.pictures,b.description,b.limits,b.stock,b.chance,b.chance1,b.chance2 from activitiesPrizes a left join prizes b on a.prizeID=b.ID where activityID=#{activityID}
and b.stock>0
order by b.chance

select count(1) from activitiesPrizes a left join prizes b on a.prizeID=b.ID where activityID=#{activityID}
and b.stock is not null
and b.stock>0


	select k.id,k.orderNumber,k.name,k.pictures,k.description,k.limits,k.stock,k.chance,k.chance1,k.chance2 
	  from prizes k 
inner join (select b.id
			  from activitiesPrizes a
   		 left join prizes b on a.prizeID=b.ID
	   		 where activityID=#{activityID}

         		and b.stock>0

			 limit #{startIndex},#{pageSize}
		   ) k1 on k.id=k1.id
	   order by k.chance
